changeSet.sql:
UPDATE study_subject SET randomization_result = (SELECT value FROM item_data WHERE event_crf_id IN (SELECT event_crf_id FROM event_crf WHERE event_crf.study_subject_id = study_subject.study_subject_id) AND item_id IN (SELECT item_id FROM item WHERE name = 'Rand_Result') AND value IS NOT NULL ORDER BY event_crf_id DESC LIMIT 1) WHERE randomization_result IS NULL;
UPDATE study_subject SET randomization_date = (SELECT date_created FROM item_data WHERE event_crf_id IN (SELECT event_crf_id FROM event_crf WHERE event_crf.study_subject_id = study_subject.study_subject_id) AND item_id IN (SELECT item_id FROM item WHERE name = 'Rand_Date') AND value IS NOT NULL AND date_updated IS NULL ORDER BY event_crf_id DESC LIMIT 1 ) WHERE randomization_date IS NULL;
UPDATE study_subject SET randomization_date = (SELECT date_updated FROM item_data WHERE event_crf_id IN (SELECT event_crf_id FROM event_crf WHERE event_crf.study_subject_id = study_subject.study_subject_id) AND item_id IN (SELECT item_id FROM item WHERE name = 'Rand_Date') AND value IS NOT NULL AND date_updated IS NOT NULL ORDER BY event_crf_id DESC LIMIT 1 ) WHERE randomization_date IS NULL;
