changeSet.sql:
CREATE OR REPLACE VIEW dn_age_days AS SELECT dn.discrepancy_note_id, CASE WHEN dn.resolution_status_id = ANY (ARRAY[1, 2, 3]) THEN DATE_PART('day', current_timestamp - (( SELECT cdn.date_created FROM discrepancy_note cdn WHERE cdn.discrepancy_note_id = ((SELECT max(idn.discrepancy_note_id) AS max FROM discrepancy_note idn WHERE idn.parent_dn_id = dn.discrepancy_note_id))))) ELSE NULL::integer END AS days, CASE WHEN dn.resolution_status_id = 4 THEN DATE_PART('day', (( SELECT cdn.date_created FROM discrepancy_note cdn WHERE cdn.discrepancy_note_id = ((SELECT max(idn.discrepancy_note_id) AS max FROM discrepancy_note idn WHERE idn.parent_dn_id = dn.discrepancy_note_id)))) - dn.date_created)
WHEN dn.resolution_status_id = ANY (ARRAY[1, 2, 3]) THEN DATE_PART('day', current_timestamp - dn.date_created)
ELSE NULL::integer END AS age FROM discrepancy_note dn WHERE dn.parent_dn_id IS NULL;
