changeSet.createProcedure:
CREATE OR REPLACE PROCEDURE move_item_level_sdv_metadata
IS
	itemFormMetadata item_form_metadata;
	sdvRequired varchar(1);
BEGIN
	FOR eventDefCRF IN (SELECT * FROM event_definition_crf WHERE parent_id IS NULL) LOOP
		FOR crfVersion IN (SELECT * FROM crf_version WHERE crf_id = eventDefCRF.crf_id) LOOP
			FOR itemFormMetadata IN (SELECT * FROM item_form_metadata) LOOP
				IF (itemFormMetadata.sdv_required = 1)
					THEN sdvRequired := ''1'';
					ELSE sdvRequired := ''0'';
				END IF;
				INSERT INTO edc_item_metadata (study_event_definition_id, event_definition_crf_id,
					crf_version_id, item_id, version, sdv_required)
					VALUES (eventDefCRF.study_event_definition_id, eventDefCRF.event_definition_crf_id,
					crfVersion.crf_version_id, itemFormMetadata.item_id, 0, sdvRequired);
			END LOOP;
		END LOOP;
	END LOOP;
	RETURN;
END;
